#include<bits\stdc++.h>using namespace std;int main(){    vector<vector<int> > mat(6, vector<int>(6,0));    for(int r=1;r<=5;r++){        for(int c=1;c<=5;c++){            cin>>mat[r][c];        }    }    int cnt = 0;    for (int r=1;r<=5;r++){        for(int c=1;c<=5;c++){            if (mat[r][c]==1){                cnt = abs(r-3) + abs(c-3);            }        }    }    cout<<cnt;    return 0;}