#include<bits\stdc++.h.>using namespace std;int main(){    vector<int> v;    string bo;    cin>>bo;    for(int i=0;i<bo.size();){        if (bo[i] == '.')            v.push_back(0);        else{            if (bo[i+1]== '.')                v.push_back(1);            else                v.push_back(2);            i+=2;            continue;        }        i++;    }    for(int i=0;i<v.size();i++){        cout<<v[i];    }    return 0;}