#include<bits\stdc++.h>using namespace std;#define ll long long int#define f(i,n,k) for(int i=n;i<k;i++)int main(){    int t;    cin>>t;    while (t--){        int n,k;        cin>>n>>k;        int arr[n];        f(i,0,n){            cin>>arr[i];        }        int cnt = 0;        int mn = (min_element(arr, arr+n) - arr);        f(i,0,n){           if (i!=mn){            while(arr[i]+arr[mn]<=k){                arr[i]+=arr[mn];                cnt++;            }           }        }        cout<<cnt<<"\n";    }    return 0;}