#include<bits\stdc++.h>using namespace std;int main(){    vector<vector<int>> grid;    grid = {{4,3,2,-1},{3,2,1,-1},{1,1,-1,-2},{-1,-1,-2,-3}};    int cnt = 0;        int n = grid[0].size() - 1;        int m = 0;        while (m<grid.size()){            while (n>=0){                if (grid[m][n] < 0){                    cnt++;                }                n = n-1;            }            m++;        }    cout<<cnt;    return 0;}