#include<bits\stdc++.h>using namespace std;int main(){    int t;    cin >> t;    while(t--){        int n;        cin>>n;        int arr[n] = {0};        for (int i=0; i<n;i++){            cin>>arr[i];        }        bool res = false;       for (int i=1; i<n;i++){            if (arr[i]>=arr[i-1]){               res = true;               break;            }        }        if (res) cout<<"YES"<<"\n";        else cout<<"NO"<<"\n";    }    return 0;}